(51, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2024-05-09 22:03:46', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'unactioned', 'woocommerce.com', '2024-05-29 17:33:44', NULL, 0, 'plain', '', 0, 1, 'info'),
(53, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2024-05-09 22:03:46', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-05-09 22:03:46', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2024-05-09 22:03:46', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2024-05-09 22:03:46', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'checkout_survey_new_users', 'survey', 'en_US', 'We\'d like your feedback on WooCommerce checkout', 'Your feedback is essential for us to improve the WooCommerce checkout and increase your conversion rate, turning more visitors into customers. Answer a few questions to help us understand your experience. It shouldn\'t take more than 5 minutes of your time.', '{}', 'pending', 'woocommerce.com', '2024-05-09 22:03:46', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'checkout_survey_new_users_no_orders', 'survey', 'en_US', 'We\'d like your feedback on WooCommerce checkout', 'Your feedback is essential for us to improve the WooCommerce checkout and increase your conversion rate, turning more visitors into customers. Answer a few questions to help us understand your experience. It shouldn\'t take more than 5 minutes of your time.', '{}', 'unactioned', 'woocommerce.com', '2024-05-09 22:03:46', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connettiti a WooCommerce.com', 'Connettiti per ottenere notifiche e aggiornamenti sui prodotti importanti.', '{}', 'unactioned', 'woocommerce-admin', '2024-05-09 22:03:49', NULL, 0, 'plain', '', 0, 1, 'info'),
(60, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Inizia ad accettare i pagamenti sul tuo negozio!', 'Ricevi i pagamenti attraverso il provider giusto per te: scegli tra oltre 100 gateway di pagamento per WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2024-05-29 17:15:27', NULL, 0, 'plain', '', 0, 1, 'info'),
(61, 'wc-admin-woocommerce-payments', 'marketing', 'en_US', 'Prova il nuovo modo di ottenere i pagamenti', 'Securely accept credit and debit cards on your site. Manage transactions without leaving your WordPress dashboard. Only with <strong>WooPayments</strong>.<br><br>Facendo clic su \"Inizia\", accetti i nostri <a href=\"https://wordpress.com/tos/\" target=\"_blank\">Termini di servizio</a>', '{}', 'unactioned', 'woocommerce-admin', '2024-05-29 17:15:27', NULL, 0, 'plain', '', 0, 1, 'info'),
(62, 'wc-update-db-reminder', 'update', 'en_US', 'Aggiornamento database WooCommerce completato', 'Aggiornamento del database di WooCommerce completato. Grazie per aver aggiornato all\'ultima versione!', '{}', 'actioned', 'woocommerce-core', '2024-05-29 17:15:27', NULL, 0, 'plain', '', 0, 1, 'info'),
(63, 'woocommerce-payfast-may-2024-update-needed', 'update', 'en_US', 'An important update is required for the Payfast Payment Gateway', '<strong>Please update to the latest version of the Payfast Payment Gateway for WooCommerce immediately to address a security vulnerability</strong>. This update is required to ensure that your transactions continue to process.', '{}', 'pending', 'woocommerce.com', '2024-05-29 17:15:28', NULL, 0, 'plain', '', 0, 0, 'info');
